import { test, expect } from '@playwright/test'

test.describe('TanStack Table Integration', () => {
  test('should display TanStack Table in project tasks view', async ({ page }) => {
    // Navigate to login page
    await page.goto('http://localhost:3003/login')

    // Fill in credentials from environment
    await page.fill('input[name="email"]', 'admin@vextrus.com')
    await page.fill('input[name="password"]', 'password')

    // Submit login form
    await page.click('button[type="submit"]')

    // Wait for navigation to dashboard
    await page.waitForURL(/\/dashboard/)

    // Navigate to projects
    await page.click('a[href="/projects"]')
    await page.waitForURL(/\/projects/)

    // Click on first project
    await page.click('table tbody tr:first-child')

    // Wait for project detail page
    await page.waitForURL(/\/projects\/[^/]+/)

    // Click on Tasks tab
    await page.click('[data-tab="tasks"], a[href*="tasks"]')

    // Verify TanStack Table is loaded (check for VextrusTable component)
    await expect(page.locator('.vextrus-table')).toBeVisible({ timeout: 10000 })

    // Check for table structure
    await expect(page.locator('[role="table"]')).toBeVisible()
    await expect(page.locator('[role="columnheader"]')).toHaveCount.greaterThan(0)

    // Verify key TanStack features
    const table = page.locator('.vextrus-table, [role="table"]').first()

    // Check for WBS column
    await expect(table.locator('text=/WBS/i')).toBeVisible()

    // Check for task title column
    await expect(table.locator('text=/Title|Task Name/i')).toBeVisible()

    // Check for status column
    await expect(table.locator('text=/Status/i')).toBeVisible()

    // Check for progress column
    await expect(table.locator('text=/Progress/i')).toBeVisible()

    // Verify NO AG-Grid classes present
    await expect(page.locator('.ag-grid')).toHaveCount(0)
    await expect(page.locator('.ag-theme-vextrus')).toHaveCount(0)

    console.log('✅ TanStack Table successfully integrated in production!')
  })

  test('should support inline editing in TanStack Table', async ({ page }) => {
    // Quick login
    await page.goto('http://localhost:3003/login')
    await page.fill('input[name="email"]', 'admin@vextrus.com')
    await page.fill('input[name="password"]', 'password')
    await page.click('button[type="submit"]')
    await page.waitForURL(/\/dashboard/)

    // Navigate to project tasks
    await page.goto('http://localhost:3003/projects')
    await page.click('table tbody tr:first-child')
    await page.waitForURL(/\/projects\/[^/]+/)
    await page.click('[data-tab="tasks"], a[href*="tasks"]')

    // Wait for table
    await page.waitForSelector('.vextrus-table, [role="table"]', { timeout: 10000 })

    // Try to click on an editable cell
    const firstEditableCell = page.locator('[role="cell"][contenteditable], [role="cell"] input').first()

    if (await firstEditableCell.count() > 0) {
      await firstEditableCell.click()
      console.log('✅ Inline editing available in TanStack Table')
    } else {
      console.log('⚠️ No editable cells found - may need to click first')
    }
  })
})
